const React = require("react");
const fetch = require("isomorphic-fetch");

const Form = ({ data }) => <div>Produto: {data.product.name}</div>;

Form.getInitialProps = async ({ req, res, query }) => {
  try {
    console.log('req+++++++++++++++', req);
    const response = await fetch(
      `https://mktp.herokuapp.com/products/${query.id}`
    );
    const data = await response.json();
    return { data };
  } catch (e) {
    console.log('error ====> : ', e);
  }
};

module.exports = Form;
