const React = require("react");
const fetch = require("isomorphic-fetch");

const Form = ({ product, options }) => {

  return (
    <>
      <div>Produto: {product.name} </div>
      {options && options.map(o => <ul>{o.name} {o.items.map(item => <li key={item._id}>{item.name}</li>)}</ul>)}
    </>
  )
}

Form.getInitialProps = async ({ req, res, query }) => {
  try {
    const response = await fetch(
      `https://mktp.herokuapp.com/form/${query.id}`
    );
    const data = await response.json();
    console.log('data', data);
    const { product, groupedOptions } = data;
    return { product, options: groupedOptions };
  } catch (e) {
    console.log('error ====> : ', e);
  }
};

module.exports = Form;
